	# Handle Host BT MAC. Agnostically.
	if [[ ! ${JOYCON_00_TYPE} == "0" ]]; then
		BT_MAC_ADDR=${JOYCON_00_HOST_MAC}
	elif [[ ! ${JOYCON_01_TYPE} == "0" ]]; then
		BT_MAC_ADDR=${JOYCON_01_HOST_MAC}
	fi
